mapper.select:
SELECT CollectionID, ItemID, CustomerID, Status, (CASE Status WHEN 'I' THEN '已收藏' WHEN 'S' THEN '加入购物车' WHEN 'D' THEN '已删除' END) AS StatusText, InUser, DATE_FORMAT(InDate, '%Y-%m-%d %H:%i:%s') AS InDate, LastEditUser, DATE_FORMAT(LastEditDate, '%Y-%m-%d %H:%i:%s') AS LastEditDate FROM Collection WHERE 1 = 1
and CustomerID=#{arg2}
and Status=#{arg3}
ORDER BY InDate DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

mapper.update:
UPDATE Collection SET
Status = #{status, jdbcType=VARCHAR},
LastEditUser = #{lastEditUser,jdbcType=VARCHAR}
WHERE CollectionID = #{collectionID,jdbcType=INTEGER}
