mapper.select:
SELECT A.ItemHotID, A.ItemID, B.ItemCode, B.ItemShortDescriptionCN, B.ItemShortDescriptionEN, C.BrandCN, C.BrandEN, B.UnitPrice4RMB, B.UnitPrice4USD, DATE_FORMAT(A.StartDate, '%Y-%m-%d %H:%i:%s') AS StartDate, DATE_FORMAT(A.EndDate, '%Y-%m-%d %H:%i:%s') AS EndDate, A.Status, (CASE A.Status WHEN 'A' THEN '激活' WHEN 'F' THEN '禁用' END) AS StatusText, A.InUser, DATE_FORMAT(A.InDate, '%Y-%m-%d %H:%i:%s') AS InDate, A.LastEditUser, DATE_FORMAT(A.LastEditDate, '%Y-%m-%d %H:%i:%s') AS LastEditDate FROM ItemHot A INNER JOIN Item B ON A.ItemID = B.ItemID INNER JOIN Brand C ON B.BrandID = C.BrandID WHERE 1 = 1
AND A.ItemID=#{arg2}
AND A.StartDate <= #{arg3}
AND A.EndDate >= #{arg4}
AND A.Status = #{arg5}
ORDER BY A.InDate DESC LIMIT #{arg0,jdbcType=INTEGER}, #{arg1,jdbcType=INTEGER};

SELECT
COUNT(1) AS TotalCount
FROM ItemHot
WHERE 1 = 1

    AND ItemID=#{arg0}


     AND StartDate <= #{arg1} 


     AND EndDate >= #{arg2} 


     AND Status = #{arg3} 
;
